mapper.select:
select * from PRODUCT


        PRODUCT_ID=#{productId}


        and PRODUCT_NAME=#{productName}


        and PRODUCT_PRICE=#{productPrice}


        and PRODUCT_NUM=#{productNum}


        and PRODUCT_REMARK=#{productRemark}



limit pageNum,pageSize

